import "./App.css";
import { useEffect, useState } from "react";
import { fabric } from "fabric";
import pen from "./pen.svg";
import eraser from "./eraser.png";

function App() {
  // const [lineWidth, setLineWidth] = useState(5);
  const [canvas, setCanvas] = useState("");
  // const [rb, setrb] = useState();
  const initCanvas = () => {
    const canvas = new fabric.Canvas("canvas");
    canvas.setHeight(window.innerHeight);
    canvas.setWidth(window.innerWidth);
    canvas.isDrawingMode = false;

    canvas.freeDrawingBrush.width = 5;

    canvas.freeDrawingBrush.color = "red";
  };
  useEffect(() => {
    setCanvas(initCanvas());
    erasers();
  }, [ini]);

  // useEffect(() => {
  // const canvas = new fabric.Canvas("canvas");
  // canvas.setHeight(window.innerHeight);
  // canvas.setWidth(window.innerWidth);
  // canvas.isDrawingMode = true;

  // canvas.freeDrawingBrush.width = 5;

  // canvas.freeDrawingBrush.color = "blue";
  // }, []);

  const erasers = () => {
    new fabric.Canvas("canvas");
    canvas.freeDrawingBrush.color = "yellow";
    canvas.isDrawingMode = true;
  };

  return (
    <div>
      <div className="headerContainer">
        <div className="box1">
          <div className="img1">
            <img style={{ width: "36px", height: "34px" }} src={pen} alt="" />
          </div>

          <div className="slidecontainer ">
            <input
              className="slider"
              type="range"
              min="3"
              max="20"
              // onChange={(e) => {
              //   setLineWidth(e.target.value);
              // }}
            />
          </div>

          <div className="colorDiv"></div>
          <div className="colorDiv"></div>
          <div className="colorDiv"></div>
          <div className="colorDiv"></div>
          <div className="colorDiv"></div>
          <div className="box1">
            <div onClick={erasers} className="img2">
              <img
                style={{ width: "36px", height: "34px", objectFit: "contain" }}
                src={eraser}
                alt=""
              />
            </div>

            <div className="slidecontainer ">
              <input
                className="slider "
                type="range"
                min="3"
                max="20"
                onChange={(e) => {
                  // SetEraser(e.target.value);
                }}
              />
            </div>
          </div>
        </div>
      </div>
      <canvas id="canvas" />
    </div>
  );
}

export default App;
