import "./App.css";
import { useEffect, useState } from "react";
import { fabric } from "fabric";
import pen from "./pen.svg";
import eraser from "./eraser.png";


const useFabric = () => {
  const canvas = React.createRef(null);
  const fabricRef = React.useCallback((element) => {
    if (!element) return canvas.current?.dispose();

    canvas.current = new fabric.Canvas(element, {backgroundColor: '#eee'});
    canvas.current.add(new fabric.Rect(
      {top: 100, left: 100, width: 100, height: 100, fill: 'red'}
    ));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  return fabricRef;
};


function App() {
  const [lineWidth, setLineWidth] = useState(5);
  const [lineColor, setLineColor] = useState("#000");

  useEffect(() => {
    const canvas = new fabric.Canvas("canvas");
    canvas.setHeight(window.innerHeight);
    canvas.setWidth(window.innerWidth);
    canvas.isDrawingMode = true;

    canvas.freeDrawingBrush.width = 5;
    // canvas.freeDrawingBrush.color = "white";

    canvas.freeDrawingBrush.color = "blue";
  }, []);

  // const pencil = (Canvas) => {
  //   canvas.isDrawingMode = true;
  // };

  const erasers = () => {
    // const canvas = new fabric.Canvas("canvas");



    canvas.freeDrawingBrush.color = "white";
  };

  return (
    <div>
      <div className="headerContainer">
        <div className="box1">
          <img className="img1" src={pen} alt="" />
          <div className="slidecontainer ">
            <input
              className="slider"
              type="range"
              min="3"
              max="20"
              onChange={(e) => {
                setLineWidth(e.target.value);
              }}
            />
          </div>

          <div className="colorDiv"></div>
          <div className="colorDiv"></div>
          <div className="colorDiv"></div>
          <div className="colorDiv"></div>
          <div className="colorDiv"></div>
          <div className="box1">
            <img onClick={erasers} className="img2" src={eraser} alt="" />
            <div className="slidecontainer ">
              <input
                className="slider "
                type="range"
                min="3"
                max="20"
                onChange={(e) => {
                  // SetEraser(e.target.value);
                }}
              />
            </div>
          </div>
        </div>
      </div>
      <canvas id="canvas" />
    </div>
  );
}

export default App;
